import axios from 'axios'
import {fetchTicketLoading,fetchTicketSuccess,fetchTicketFail,searchTickets} from "./ticketsSlice"

export const fetchAllTickets = () => async (dispatch) => {
  dispatch(fetchTicketLoading());
  try {
    // const result = await getAllTickets();
    // result.data.result.length &&
    //   dispatch(fetchTicketSuccess(result.data.result));
    const result=await axios.get(
        'http://localhost:5000/v1/ticket',
        {headers:{
            Authorization:'Bearer eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJlbWFpbCI6ImFuamFsaS5zaGFybWFAMTIzNC5jb20iLCJpYXQiOjE3NTEyNjM0MTgsImV4cCI6MTc1MTM0OTgxOH0.6VS875fUvnVjul_XDZzzEa9A7bMSzuk1mBsBNOvPUJI'
        }}
    )
    console.log(result)
    dispatch(fetchTicketSuccess(result.data.result));
  } catch (error) {
    dispatch(fetchTicketFail(error.message));
  }
};

export const filterSerachTicket = (str) => (dispatch) => {
  dispatch(searchTickets(str));
};
