import React, { useState } from 'react';
import { Row, Col, Container, Form, Button, Spinner, Alert } from 'react-bootstrap';
import { loginPending, loginSuccess, loginFail } from './loginSlice';
import { useDispatch, useSelector } from 'react-redux';
import { userLogin } from '../../api/userApi';

const Login = ({ formSwitcher }) => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const dispatch = useDispatch();
  const { isLoading, isAuth, error } = useSelector(state => state.login);

  const handleOnchange = e => {
    const { name, value } = e.target;
    switch (name) {
      case 'email':
        setEmail(value);
        break;
      case 'password':
        setPassword(value);
        break;
      default:
        break;
    }
  };

  const handleOnSubmit = async e => {
    e.preventDefault();
    if (!email || !password) {
      return alert('fill up all form');
    }
    dispatch(loginPending());
    try {
      const isAuth = await userLogin({ email, password });
      if (isAuth.state === 'error') {
        dispatch(loginFail(isAuth.message));
      }
      if (isAuth.status === 'error') {
        return dispatch(loginFail(isAuth.message));
      }
      dispatch(loginSuccess());
    } catch (error) {
      dispatch(loginFail(error.message));
    }
  };

  return (
    <Container className="py-5">
      <Row className="justify-content-center">
        <Col xs={12} sm={10} md={8} lg={6}>
          <h1 className="text-info text-center mb-4">Client Login</h1>
          <hr />
          {error && (
            <Alert variant="danger" className="mt-3 mb-4 text-center shadow-sm">
              {error}
            </Alert>
          )}
          <Form autoComplete="off" onSubmit={handleOnSubmit}>
            <Form.Group>
              <Form.Label>Email Address</Form.Label>
              <Form.Control
                type="email"
                name="email"
                value={email}
                onChange={handleOnchange}
                placeholder="Enter Email"
                required
              />
            </Form.Group>
            <Form.Group className="mt-3">
              <Form.Label>Password</Form.Label>
              <Form.Control
                type="password"
                name="password"
                value={password}
                onChange={handleOnchange}
                placeholder="Enter Password"
                required
              />
            </Form.Group>
            <div className="d-grid gap-2 mt-4">
              <Button type="submit" variant="primary" disabled={isLoading}>
                {isLoading ? <Spinner size="sm" animation="border" /> : 'Login'}
              </Button>
            </div>
          </Form>
          <hr />
          <div className="text-center mt-3">
            <a
              href="#!"
              onClick={() => formSwitcher('reset')}
              className="btn btn-outline-info btn-sm rounded-pill px-4 shadow-sm"
            >
              Forgot Password?
            </a>
          </div>
        </Col>
      </Row>
    </Container>
  );
};

export default Login;
